import{_ as t,c as r,o as a,j as o,ag as n,a as s}from"./chunks/framework.S-Qvb3wi.js";const m=JSON.parse('{"title":"Moderator Guide","description":"","frontmatter":{"title":"Moderator Guide","lang":"en-GB","outline":"deep","lastUpdated":true,"search":false},"headers":[],"relativePath":"guide/moderator.md","filePath":"guide/moderator.md","lastUpdated":1757410021000}'),i={name:"guide/moderator.md"};function l(d,e,c,u,h,p){return a(),r("div",null,e[0]||(e[0]=[o("h1",{moderator:"",id:"moderator-s-guide",tabindex:"-1"},[s("Moderator's Guide "),o("a",{class:"header-anchor",href:"#moderator-s-guide","aria-label":`Permalink to "Moderator's Guide {moderator}"`},"â€‹")],-1),n('<h2 id="general-information" tabindex="-1">What is the general information about moderation? <a class="header-anchor" href="#general-information" aria-label="Permalink to &quot;What is the general information about moderation? {#general-information}&quot;">â€‹</a></h2><ul><li><strong>Read and understand all server rules</strong> (rough memorization recommended)</li><li><strong>Set server notifications</strong> to <em>&quot;Mentions Only&quot;</em> (recommended) or <em>&quot;All Messages&quot;</em></li><li><strong>Know all server channels</strong> and their purposes</li></ul><h3 id="staff-strikes-system" tabindex="-1">Staff Strikes System <a class="header-anchor" href="#staff-strikes-system" aria-label="Permalink to &quot;Staff Strikes System&quot;">â€‹</a></h3><ul><li>Given for improper staff behavior or rule violations</li><li>3 strikes = demotion</li></ul><h3 id="chat-management" tabindex="-1">Chat Management <a class="header-anchor" href="#chat-management" aria-label="Permalink to &quot;Chat Management&quot;">â€‹</a></h3><ul><li>Direct conversations to appropriate channels</li><li><strong>Prevent</strong> spam/flooding with gifs/images in chat channels</li></ul><h3 id="ticket-support" tabindex="-1">Ticket Support <a class="header-anchor" href="#ticket-support" aria-label="Permalink to &quot;Ticket Support&quot;">â€‹</a></h3><ul><li><strong>Respond quickly</strong> to support tickets</li><li>ONE staff member per ticket only</li><li>First staff member handles and closes the ticket</li></ul><h3 id="moderation-actions" tabindex="-1">Moderation Actions <a class="header-anchor" href="#moderation-actions" aria-label="Permalink to &quot;Moderation Actions&quot;">â€‹</a></h3><ul><li>Use punishment commands where the incident occurred or in <code>#ãƒ»staff-cmd</code></li><li>Always check previous warnings or cases with <code>!cases @mention</code></li></ul><h3 id="general-guidelines" tabindex="-1">General Guidelines <a class="header-anchor" href="#general-guidelines" aria-label="Permalink to &quot;General Guidelines&quot;">â€‹</a></h3><ul><li>Maintain a family-friendly environment</li><li>Use common sense</li><li><strong>Have fun ðŸ˜ƒ</strong></li></ul><h2 id="punishment" tabindex="-1">What are the procedures for mod? <a class="header-anchor" href="#punishment" aria-label="Permalink to &quot;What are the procedures for mod? {#punishment}&quot;">â€‹</a></h2><h3 id="verbal-warnings" tabindex="-1"><strong>Verbal</strong> <strong>Warnings</strong> <a class="header-anchor" href="#verbal-warnings" aria-label="Permalink to &quot;**Verbal** __Warnings__&quot;">â€‹</a></h3><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Trainees! Please look at how current Moderators give verbal warnings and take inspiration from what they&#39;re doing and saying. Please don&#39;t use AI, it is obvious most of the time.</p></div><ul><li><strong>Being Disrespectful:</strong></li></ul><blockquote><p>Verbal warn, if they continue: 12 hour mute.</p></blockquote><ul><li><strong>Arguments:</strong></li></ul><blockquote><p>Send <code>!fight</code>, if they continue: 30 minute mute.</p></blockquote><ul><li><strong>NSFW Conversations:</strong></li></ul><blockquote><p>Verbal warn.</p></blockquote><ul><li><strong>Talking in a different Language:</strong></li></ul><blockquote><p>Verbal warn.</p></blockquote><h3 id="normal-warnings" tabindex="-1"><strong>Normal</strong> <strong>Warnings</strong> <a class="header-anchor" href="#normal-warnings" aria-label="Permalink to &quot;**Normal** __Warnings__&quot;">â€‹</a></h3><ul><li><strong>Asking for Pirated Content:</strong></li></ul><blockquote><p>Verbal Warn + <code>piracy</code>.</p></blockquote><ul><li><strong>Sharing Pirated Content:</strong></li></ul><blockquote><p>Reason: <code>sendingpiracy</code></p></blockquote><ul><li><strong>Spamming same Message in multiple Channels:</strong></li></ul><blockquote><p>Reason: <code>channelspam</code>.</p></blockquote><ul><li><strong>Pinging Helpers in non-help Channels:</strong></li></ul><blockquote><p>Verbal Warn + <code>outsideping</code>.</p></blockquote><ul><li><strong>Mild NSFW:</strong></li></ul><blockquote><p>Reason: <code>mildnsfw</code>.</p></blockquote><ul><li><strong>Self Promoting outside of <a href="https://discord.com/channels/1118862694980788276/1130552243498791012" target="_blank" rel="noreferrer">#ãƒ»edits</a>:</strong></li></ul><blockquote><p>Verbal Warn + <code>selfpromo</code>.</p></blockquote><ul><li><strong>Advertising Payhip in <a href="https://discord.com/channels/1118862694980788276/1130552243498791012" target="_blank" rel="noreferrer">#ãƒ»edits</a>:</strong></li></ul><blockquote><p>Verbal Warn + <code>payhippromo</code>.</p></blockquote><ul><li><strong>Requests outside of <a href="https://discord.com/channels/1118862694980788276/1253224549152198777" target="_blank" rel="noreferrer">#ãƒ»request</a>:</strong></li></ul><blockquote><p>Verbal Warn + <code>outsidereq</code>.</p></blockquote><ul><li><strong>Posting their ad more than once:</strong></li></ul><blockquote><p>Reason: <code>doublead</code>.</p></blockquote><ul><li><strong>Rude behavior, harassment or instigating arguments:</strong></li></ul><blockquote><p>Reason: <code>toxicity</code>.</p></blockquote><ul><li><strong>Leaving tickets empty or failing to respond:</strong></li></ul><blockquote><p>Reason: <code>ticket</code>.</p></blockquote><ul><li><strong>Ping staff for requests:</strong></li></ul><blockquote><p>Reason: <code>requestping</code>.</p></blockquote><h3 id="kicks" tabindex="-1"><strong>Ki</strong><strong>cks</strong> <a class="header-anchor" href="#kicks" aria-label="Permalink to &quot;**Ki**__cks__&quot;">â€‹</a></h3><p><em><strong>Required Role: Moderator or higher.</strong></em></p><ul><li><strong>Selling/Buying any kind of Accounts:</strong></li></ul><blockquote><p>Reason: <code>accounts</code>.</p></blockquote><ul><li><strong>DM Advertising:</strong></li></ul><blockquote><p>Reason: <code>dmad</code>.</p></blockquote><h3 id="all-bans" tabindex="-1"><strong>All</strong> <strong>Bans</strong> <a class="header-anchor" href="#all-bans" aria-label="Permalink to &quot;**All** __Bans__&quot;">â€‹</a></h3><p><em><strong>Required Role: Moderator or higher. Proof Required for ALL bans.</strong></em></p><ul><li><strong>Scamming in trade:</strong></li></ul><blockquote><p>Reason: <code>trade</code>.</p></blockquote><ul><li><strong>Scammers:</strong></li></ul><blockquote><p>Reason: <code>make your own</code>.</p></blockquote><ul><li><strong>Sending Nudity, Gore, etc:</strong></li></ul><blockquote><p>Reason: <code>nsfwban</code>.</p></blockquote><ul><li><strong>Slurs:</strong></li></ul><blockquote><p>Reason: <code>hatespeech</code>.</p></blockquote><ul><li><strong>Being under the age of 13:</strong></li></ul><blockquote><p>Reason: <code>underage</code>.</p></blockquote><ul><li><strong>Doxxing:</strong></li></ul><blockquote><p>Reason: <code>doxxing</code>.</p></blockquote><ul><li><strong>Raiding:</strong></li></ul><blockquote><p>Reason: <code>raiding</code>.</p></blockquote><ul><li><strong>Using alternate accounts to bypass bans or punishments:</strong><br> Reason: <code>alts</code>.</li><li><strong>Sharing password:</strong></li></ul><blockquote><p>Reason: <code>password</code>.</p></blockquote><ul><li><strong>Sending phishing links or harmful files:</strong></li></ul><blockquote><p>Reason: <code>hacked</code>.</p></blockquote><ul><li><strong>Scamming in trades:</strong></li></ul><blockquote><p>Reason: <code>tradescam</code>.</p></blockquote><ul><li><strong>Disapproving act of leaking:</strong></li></ul><blockquote><p>Reason: <code>hypocrisy</code>.</p></blockquote><ul><li><strong>Racism:</strong></li></ul><blockquote><p>Reason: <code>racism</code>.</p></blockquote><h3 id="other-info" tabindex="-1"><strong>Other</strong> / <strong>Info</strong> <a class="header-anchor" href="#other-info" aria-label="Permalink to &quot;**Other** / __Info__&quot;">â€‹</a></h3><ul><li>Avoid warning new accounts, just redirect them (give them a verbal warn). If they (for example) misuse a channel more than once you can warn them.</li><li>If you (for whatever reason) need to make your own reason, use <code>!warn (userid) (reason)</code>. Do <strong>not</strong> do this without asking other Moderators (or higher) first.</li></ul><blockquote><p>Words that look like <code>this</code> are predefined reasons. Learn how to use them here: <a href="#what-are-the-predefined-reasons"><code>Predefined Reasons</code></a>. <strong>Don&#39;t</strong> warn <strong>new members</strong> for posting something in the wrong channel. Redirect them instead.</p></blockquote><p><strong>Ban Duration Guide</strong></p><table tabindex="0"><thead><tr><th>Type</th><th style="text-align:center;">Duration</th></tr></thead><tbody><tr><td><strong>DM Advertising</strong></td><td style="text-align:center;">1-Day</td></tr><tr><td><strong>Suicide Encouragement</strong></td><td style="text-align:center;">1-Week</td></tr><tr><td><strong>Doxxing</strong></td><td style="text-align:center;">Permanent</td></tr><tr><td><strong>NSFW Content</strong></td><td style="text-align:center;">Permanent</td></tr><tr><td><strong>Racism</strong></td><td style="text-align:center;">Permanent</td></tr><tr><td>Always check user history with <code>!cases (user)</code> before taking action</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="what-are-the-predefined-reasons" tabindex="-1">What are the Predefined Reasons? <a class="header-anchor" href="#what-are-the-predefined-reasons" aria-label="Permalink to &quot;What are the Predefined Reasons?&quot;">â€‹</a></h2><h3 id="how-to-use-predefined-reasons" tabindex="-1"><strong>How to use</strong> <strong>Predefined Reasons</strong> <a class="header-anchor" href="#how-to-use-predefined-reasons" aria-label="Permalink to &quot;**How to use** __Predefined Reasons__&quot;">â€‹</a></h3><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Predefined Reasons work for <strong>Warns</strong>, <strong>Timeouts</strong>, <strong>Kicks</strong> and <strong>Bans</strong>. I will use <code>!warn</code> as an example, but it works the same with the other commands.</p></div><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>!warn @617249450867425301 selfpromo</span></span></code></pre></div><p><strong><code>!warn [userid] [duration] [predefinedreason]</code></strong></p><h3 id="predefined-reasons" tabindex="-1"><strong>Predefined</strong> <strong>Reasons</strong> <a class="header-anchor" href="#predefined-reasons" aria-label="Permalink to &quot;**Predefined** __Reasons__&quot;">â€‹</a></h3><blockquote><p><code>piracy</code><br> Asking for pirated content (e.g. cracked software, full movies/shows) is not allowed and goes against Discordâ€™s Terms of Service. Please read our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a>.</p></blockquote><blockquote><p><code>requestping</code><br> Please donâ€™t ping staff for requests. Requests are fulfilled based on availability, and upvotes do not guarantee that the request will be leaked.</p></blockquote><blockquote><p><code>selfpromo</code><br> Self-promotion is only allowed in <a href="https://discord.com/channels/1118862694980788276/1130552243498791012" target="_blank" rel="noreferrer">ãƒ»edits</a>. Promoting your socials elsewhere is not allowed. Please read our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a>.</p></blockquote><blockquote><p><code>payhippromo</code><br> Promoting your payhip elsewhere is not allowed. Please read our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a>.</p></blockquote><blockquote><p><code>outsidereq</code><br> Requests are only allowed in our request channels. Posting them elsewhere isnâ€™t allowed. Please read our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a>. You can create a request here: <a href="https://discord.com/channels/1118862694980788276/1253224549152198777" target="_blank" rel="noreferrer">ãƒ»request</a>.</p></blockquote><blockquote><p><code>doublead</code><br> Please don&#39;t post your ad more than once. You can find our rules pinned in <a href="https://discord.com/channels/1118862694980788276/1310079315227967588" target="_blank" rel="noreferrer">ãƒ»commissions</a>.</p></blockquote><blockquote><p><code>channelspam</code><br> Please refrain from spamming the same message in multiple channels, or you will get kicked. Please read our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a>.</p></blockquote><blockquote><p><code>mildnsfw</code><br> Please stop sending NSFW content. Doing this again will result in a permanent ban. Please read our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a>.</p></blockquote><blockquote><p><code>sendingpiracy</code><br> Sending pirated content (e.g. cracked software, full movies/shows) is not allowed and goes against Discordâ€™s Terms of Service. If you do this again, you will be banned. Please read our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a>.</p></blockquote><blockquote><p><code>accounts</code><br> Selling or Buying accounts is against our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a> and Discord&#39;s ToS.</p></blockquote><blockquote><p><code>dmad</code><br> DM advertising is not allowed. You are banned as a result.</p></blockquote><blockquote><p><code>nsfwban</code><br> Posting NSFW content in non-age-restricted channels violates Discordâ€™s Terms of Service. You are banned as a result.</p></blockquote><blockquote><p><code>hatespeech</code><br> Hate speech of any kind is not tolerated and is against Discordâ€™s Terms of Service. You are banned as a result.</p></blockquote><blockquote><p><code>trade</code><br> Scamming in trades is not tolerated. You are banned as a result. Please read our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a>.</p></blockquote><blockquote><p><code>underage</code><br> You are under the age of 13, which violates Discordâ€™s Terms of Service. You can appeal this ban once you are 13 or older.</p></blockquote><blockquote><p><code>hacked</code><br> Banned for sending phishing links or harmful files. If your account was hacked, please secure it and appeal for an unban.</p></blockquote><blockquote><p><code>ticket</code><br> Leaving tickets empty or failing to respond wastes staff time. Please only create a ticket if youâ€™re sure about your request. Multiple violations will result in a ban.</p></blockquote><blockquote><p><code>password</code><br> Sharing password access to our preset files outside of the server is strictly prohibited. You are banned as a result.</p></blockquote><blockquote><p><code>toxicity</code><br> Excessive rude behavior, harassment, or instigating arguments is not tolerated. Please remain respectful &amp; read our <a href="https://discord.com/channels/1118862694980788276/1250087429763760250" target="_blank" rel="noreferrer">ãƒ»rules</a>.</p></blockquote><blockquote><p><code>alts</code><br> Using alternate accounts to bypass bans or punishments is prohibited. You are banned as a result.</p></blockquote><blockquote><p><code>impersonation</code><br> Pretending to be staff or another member is not tolerated. Please stop or you will be banned.</p></blockquote><blockquote><p><code>doxxing</code><br> Sharing personal information (doxxing) is strictly prohibited and results in an instant ban.</p></blockquote><blockquote><p><code>raiding</code><br> Coordinated raids or spamming with others is not allowed. You are banned as a result.</p></blockquote><blockquote><p><code>hypocrisy</code><br> Using leaks while disapproving of leaking is hypocrisy and results in an immediate ban. This also applies if you defend your friendsâ€™ stuff while benefiting from othersâ€™ leaks.</p></blockquote><blockquote><p><code>racism</code><br> Racism of any kind is strictly prohibited and violates both our rules and Discordâ€™s Terms of Service. You are banned as a result.</p></blockquote><h2 id="responsibilities" tabindex="-1">What are the responsibilities of a moderator? <a class="header-anchor" href="#responsibilities" aria-label="Permalink to &quot;What are the responsibilities of a moderator? {#responsibilities}&quot;">â€‹</a></h2><h3 id="handling-tickets" tabindex="-1"><strong>Handling Tickets</strong> <a class="header-anchor" href="#handling-tickets" aria-label="Permalink to &quot;**Handling Tickets**&quot;">â€‹</a></h3><ul><li><p>Tickets are a primary way for members to seek support. These include:</p><ul><li>Claim level &amp; boost perks</li><li>Report a member</li><li>Questions or concerns</li><li>Applying for staff or Partner Manager</li><li>Asking to partner</li><li>Sponsoring an event or giveaway</li></ul></li><li><p><strong>How to handle tickets:</strong></p><ul><li><strong>Respond Quickly:</strong> Address tickets as soon as possible.</li><li><strong>One Moderator per Ticket:</strong> Only one staff member should handle each ticket. If you&#39;re unable to assist further, transfer the ticket to another staff member using <code>/transfer</code> command.</li><li><strong>Professional Tone:</strong> Always respond politely, respectfully, and professionally.</li><li><strong>Closing Tickets:</strong> Close tickets once they are resolved. This includes when the member leaves the ticket empty/no response.</li></ul></li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>You are <strong>required</strong> to warn any member that leaves a ticket empty or no follow-up.</p></div><h3 id="deleting-messages" tabindex="-1">Deleting Messages <a class="header-anchor" href="#deleting-messages" aria-label="Permalink to &quot;Deleting Messages&quot;">â€‹</a></h3><p><strong>When to Delete Messages:</strong></p><ul><li>NSFW content</li><li>Hate speech</li><li>Racist or discriminatory remarks</li><li>Spam or self-promotion</li><li>Messages in the wrong channels</li></ul><h3 id="activity-expectations" tabindex="-1">Activity Expectations <a class="header-anchor" href="#activity-expectations" aria-label="Permalink to &quot;Activity Expectations&quot;">â€‹</a></h3><p><strong>Minimum Activity:</strong> 24-hour per week</p><p><strong>Trial Moderator Requirements:</strong></p><ul><li>Be active and engage in the community.</li><li>Assist members and moderate channels.</li><li>Use at least 5 mod commands appropriately.</li></ul><h3 id="general-responsibilities" tabindex="-1">General Responsibilities <a class="header-anchor" href="#general-responsibilities" aria-label="Permalink to &quot;General Responsibilities&quot;">â€‹</a></h3><p><strong>Core Duties:</strong></p><ul><li>Enforce server rules and guidelines.</li><li>Monitor messages and remove rule-breaking content.</li><li>Handle user reports, tickets, and conflicts.</li><li>Maintain a welcoming and positive community atmosphere.</li></ul><p><strong>Key Skills:</strong></p><ul><li>Strong communication skills.</li><li>Conflict resolution.</li><li>Fair judgment and empathy.</li><li>Patience and professionalism.</li></ul><h2 id="moderation-commands" tabindex="-1">What are the moderation commands? <a class="header-anchor" href="#moderation-commands" aria-label="Permalink to &quot;What are the moderation commands? {#moderation-commands}&quot;">â€‹</a></h2><ul><li><code>!warn @mention/id reason</code> - Issues a warning to the mentioned user.</li><li><code>!sp @mention/id proof</code> - Set proof to the most recent case tied to your action. (Attach a file or link/message link)</li><li><code>!mute @mention/id 1h reason</code> - Mutes the mentioned user for 1 hour. <em>(Default: 1 hour)</em></li><li><code>!tempmute @mention/id 1h reason</code> - Temporarily mutes the mentioned user for 1 hour. <em>(Default: 1 hour)</em></li><li><code>!ban @mention/id reason</code> - Bans the mentioned user. <em>(Default: No duration)</em></li><li><code>!tempban @mention/id 1d reason</code> - Temporarily bans the mentioned user for 1 day. <em>(Default: Unknown duration)</em></li><li><code>!warnings @mention/id</code> - Displays the warning history of the mentioned user.</li><li><code>!cases @mention/id</code> - Displays the cases history of the mentioned user.</li></ul><p><strong>Note:</strong> These are examples of the basic commands you&#39;ll use for moderation.</p><h2 id="server-related" tabindex="-1">Server Related <a class="header-anchor" href="#server-related" aria-label="Permalink to &quot;Server Related {#server-related}&quot;">â€‹</a></h2><h3 id="ticket" tabindex="-1">Ticket <a class="header-anchor" href="#ticket" aria-label="Permalink to &quot;Ticket&quot;">â€‹</a></h3><p>If you claimed a ticket, please warn the member and close the ticket if no response are given for over 12 hours. This would help the server be less clogged.</p><h3 id="booster-role" tabindex="-1">Booster Role <a class="header-anchor" href="#booster-role" aria-label="Permalink to &quot;Booster Role&quot;">â€‹</a></h3><p>When a user opens a ticket to claim their custom role, please do the following:</p><ol><li><strong>Verify Booster Status</strong>: First, confirm that the member has boosted the server at least <strong>twice</strong> (<code>2x</code>). This is a strict requirement.</li><li><strong>Whitelist the Member</strong>: Use the new command to grant them access to the role creation panel. (<code>/boosterrole whitelist</code>)</li></ol><p><strong>Command:</strong> Using this command on a valid 2x booster will allow them to use <code>/boosterrole manage</code>. Please familiarize yourselves with this new procedure.</p><h3 id="duplicate-requests" tabindex="-1">Duplicate requests <a class="header-anchor" href="#duplicate-requests" aria-label="Permalink to &quot;Duplicate requests&quot;">â€‹</a></h3><p>Request duplicates can be monitored through: <a href="https://discord.com/channels/1118862694980788276/1395469507014951063" target="_blank" rel="noreferrer">#ãƒ»request-log</a>. You can simply <strong>Delete</strong> the detected duplicate request, or <strong>Ignore</strong> it (if it made some mistake or other reasons).</p><h3 id="unable-to-click-download-button-in-presets" tabindex="-1">Unable to click download button in Presets <a class="header-anchor" href="#unable-to-click-download-button-in-presets" aria-label="Permalink to &quot;Unable to click download button in Presets&quot;">â€‹</a></h3><p>If buttons in certain threads in <code>#ãƒ»presets</code> went greyed out, it means the thread is archived. You may run the command <code>/reopen</code> to unarchive all archived threads.</p><h3 id="_1-month-old-account-restriction-whitelist" tabindex="-1">1-month old account restriction whitelist <a class="header-anchor" href="#_1-month-old-account-restriction-whitelist" aria-label="Permalink to &quot;1-month old account restriction whitelist&quot;">â€‹</a></h3><p>To whitelist a member to bypass the <code>#ãƒ»preset</code> download 1-month old account restriction, follow the steps below to ensure they&#39;re legit and not suspicious.</p><p><strong>Requirements to Get Whitelisted</strong></p><ul><li>The member <strong>must provide</strong>: <ul><li>A screenshot of their TikTok profile.</li><li>The account must be an editing account.</li><li>Make sure the screenshot clearly shows &quot;<strong>Edit profile</strong>&quot; â€” this confirms itâ€™s their own account.</li></ul></li></ul><p>If you donâ€™t see &quot;Edit profile&quot;, itâ€™s most likely fake.</p><p><strong>How to Check If They&#39;re Suspicious</strong></p><ul><li>Before approving, do the following: <ul><li>Search up their TikTok username directly on TikTok.</li><li>Check if the account looks real: consistent content, normal follower/following ratio, not freshly made.</li><li>If something feels off (no posts, suspicious bio, reused content), ask follow-up questions or deny the request. The command: <code>/leak whitelist</code></li></ul></li></ul>',153)]))}const b=t(i,[["render",l]]);export{m as __pageData,b as default};
